import React from "react";
import { gql } from "apollo-boost";
import { useQuery } from "react-apollo-hooks";
import Loader from "../Components/Loader";

const FEED_QUERY = gql`
    {
        seeFeed{
            id
            location
            caption
            user{
                id
                avatar
                username
            }
            files{
                id
                url
            }
            likeCount
            isLiked
            comments{
                id
                text
                user{
                    id
                    username
                }
            }
            createdAt
        }
    }
`;

export default () => {
    const { data, loading } = useQuery(FEED_QUERY);
    if(loading){
        return <Loader />;
    } else {
        return null;
    }

}
